(window.webpackJsonp=window.webpackJsonp||[]).push([[35],{416:function(v,_,t){"use strict";t.r(_);var r=t(44),s=Object(r.a)({},(function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h2",{attrs:{id:"你真的理解什么是-财富自由-吗"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#你真的理解什么是-财富自由-吗"}},[v._v("#")]),v._v(" 你真的理解什么是”财富自由“吗？")]),v._v(" "),t("h3",{attrs:{id:"一个更源头的问题"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#一个更源头的问题"}},[v._v("#")]),v._v(" 一个更源头的问题")]),v._v(" "),t("p",[v._v("作为《通往财富自由之路》的第一篇文章，这篇东西理所当然要讲一下什么是财富自由。")]),v._v(" "),t("p",[v._v("可是我想通过这一篇，和大家聊一个更源头的问题——")]),v._v(" "),t("p",[t("strong",[v._v("有些目标，比如眼前这闪着光芒的”财富自由“，我们明明感觉自己为之使出了浑身气力，但为什么这么多年过去，却没有以我们期望的速度向它靠近？")])]),v._v(" "),t("p",[v._v("年轻的时候，我们大都经历过，非常清楚自己不想要什么。比如刚出学校的时候，我们只知道自己不想被束缚、不想低人一等……但我们并不知道自己应该要什么，甚至不知道自己想要什么。于是绝大多数年轻人在描述自己的理想时，翻过来覆过去，只有那一句话："),t("strong",[v._v("我要变得很牛逼！——")]),v._v(" 至于怎么样才算是”牛逼”，接着问下去一定卡壳。")]),v._v(" "),t("p",[v._v("这种尴尬反映出了"),t("strong",[v._v("绝大多数人在追求某个东西的时候，可能连那个东西的定义都不清楚。")])]),v._v(" "),t("p",[v._v("于是你在回头去想那些你非常想要、也努力奋斗的事，为什么总是无法靠它更近，那就是因为，你还在做无头苍蝇似的飞行，因为你连你要的究竟是什么都不知道……")]),v._v(" "),t("p",[v._v("可是我想告诉你的是，这只不过是“嫩“的一种表现：只知道自己不想要什么，却不知道自己想要什么。")]),v._v(" "),t("p",[v._v("语言学家告诉我们，**如果我们脑子里没有一个概念的话，我们的脑子就倾向于不去想那个事情；如果一个民族的语言里空缺某个概念，那么整个民族就倾向于从未想过那个事情。**没错，语言对人就是有如此强大的反向塑造能力（关于广泛存在的反向塑造，我们会在之后详细说）。比如绝大多数欧美国家的人不可能想到”我上火了“，因为在他们的世界里，没有”上火“这个概念，所以他们不仅不知道，而且完全想不到自己会”上火“，你顶多可以拿他们已经知道的”发言“这个概念去跟他们解释。")]),v._v(" "),t("p",[v._v("如果脑子里的一个概念不准确、或者没有准确、正确的定义，那么我们必然没办法准确、正确地继续思考下去。进而产生的连锁反应是，因为定义不准确，所以思考范围模糊，选择依据缺失，进而行动错误……进而影响整个生活。")]),v._v(" "),t("p",[v._v("举另一个简单的例子：分享这个概念，究竟是什么？")]),v._v(" "),t("p",[v._v('大多数人没认真想过这个问题，以为所谓的"分享"就是把好东西拿出来和大家一块享受，只有少数人认真想过，这个定义是有问题的————好东西必须是你自己的，然后你再拿出来与大伙一块儿享受，那才是真正的分享。很多人是把别人的好东西拿过去，跟大伙一块享受，那不叫分享，那是”慷他人之慨“，不是吗？')]),v._v(" "),t("p",[v._v("从这个角度望过去，你就能理解为什么有些人盗版那么理直气壮了，说穿了，就是他们脑子不清楚，或者说脑子糊涂，他们被自己脑子里的错误定义误导了。他们甚至不是坏人，因为若是他们想明白了，其实是不可能、也不好意思理直气壮的。")]),v._v(" "),t("p",[v._v("虽然一生中你将会有无数次不知道某个概念，或者误解了很多东西的正确定义，但是你依然有更多的机会，被人教会、或者自己教会自己，最终那个正确的定义。")]),v._v(" "),t("h3",{attrs:{id:"找到它的定义"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#找到它的定义"}},[v._v("#")]),v._v(" 找到它的定义")]),v._v(" "),t("p",[v._v("直到想明白这个问题，你才会发现，如果你真的”想要“财富自由，那么你就需要理解财富自由，像海洋理解河流一样，直到你将这个概念理解得如吃饭睡觉一样透彻之后，你才有了可以加速向它靠近的前提。")]),v._v(" "),t("p",[v._v("财富自由（Financial Freedom）是一个在很多人心尖上发光的词，我也琢磨了很多年，一直没人能给我一个清晰、准确、正确的定义。我看了维基百科：")]),v._v(" "),t("blockquote",[t("p",[t("strong",[v._v("财富自由是指,你无须为生活开销而努力为钱工作的状态。简单地说，你的资产产生的被动收入必须至少等于或超过你的日常开支,这是我们大多数人最渴望达到的状态，如果进入这种状态，我们就可以称之为!退休或其他各种名称。")])])]),v._v(" "),t("p",[v._v("我勉强认为它做到了清晰，但这远远不够，因为这样的定义，让我完全看不出指导意义，反复读过之后，也不知道下一步该干什么……这不行。")]),v._v(" "),t("p",[v._v("直到有一天，我找到了能真正影响行动的关键，我们要的自由，最根本不是财富，"),t("strong",[v._v("财富只是工具，我们要的自由，本质是时间的自主权")]),v._v("。所以我自己重新把财富自由的定义提炼为：")]),v._v(" "),t("p",[v._v("所谓的个人财富自由，指的是某个人再也不用为了满足生活必需而出售自己的时间了。")]),v._v(" "),t("p",[v._v("够简洁、准确、正确，而且有指导意义。")]),v._v(" "),t("p",[v._v("我们一生在做的所有事，都在消耗只去不回的时间。如果你做的事能让你有收获，那么你付出的时间可以视作”被出售“。我们每个人做的所有奋斗的本质，都是让自己出售的时间单价更高。")]),v._v(" "),t("p",[v._v("你会想到一种非常直接的提高时间单价的方法： 打工的人会首先想到要提高自己的时薪。但是人群中的少部分人会对这个概念产生思考上的飞跃————提高时间单价的方法，就是想办法把原来一份的时间，卖出更多份。")])])}),[],!1,null,null,null);_.default=s.exports}}]);