(window.webpackJsonp=window.webpackJsonp||[]).push([[6],{355:function(v,_,t){v.exports=t.p+"assets/img/1.16178c12.png"},365:function(v,_,t){"use strict";t.r(_);var s=t(45),p=Object(s.a)({},(function(){var v=this,_=v.$createElement,s=v._self._c||_;return s("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[s("h1",{attrs:{id:"写作"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#写作"}},[v._v("#")]),v._v(" 写作")]),v._v(" "),s("h2",{attrs:{id:"资源配置习惯决定人生成就"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#资源配置习惯决定人生成就"}},[v._v("#")]),v._v(" 资源配置习惯决定人生成就")]),v._v(" "),s("p",[v._v("我从自己的生活中发现一些现象，当自己处于巨大的压力之下时，往往希望寻求突破，进而能够进行比较深入的思考，执行力也变强了，反倒是在没有压力的状态下，很难静下心来去和自己做深度沟通，太过舒适的生活容易滋生懈怠情绪，目标感不强，执行力会打折扣。")]),v._v(" "),s("p",[v._v("我觉得大多数人可能和我类似，忙碌和压力之下容易陷于负面情绪中，比较典型的就是焦虑，些许的焦虑感会促进对于当前生存环境的担忧，进而让自己更加清醒。")]),v._v(" "),s("p",[v._v("我曾经和朋友聊过关于工作和生活的平衡问题，他的一番话给我了启发，他说，我们工作的本质其实就是为了更好的生活，现在每天加班忙碌有点本末倒置了，这话倒也没错，我确实很少看到他因为工作的问题弄的焦头烂额，到点下班，把工作好生活分割的比较清晰，我其实是很羡慕他的，这叫做看的开。")]),v._v(" "),s("p",[v._v("我在这方面做得不是很好，因为一些特殊的原因，我在国企上班的那段时间不得不承担一些额外的工作量，每天早上到公司的第一件事就是处理运营群里的问题，上午开会，下午开会，本身自己身上还有开发任务，这就造成了一个很尴尬的局面，只有等到晚上大家都下班了，各种支撑群的消息基本平息了才有时间做自己的开发工作，白天的工作虽然忙碌，但是并不会从数据中统计出来，我倒不是抱怨什么，任何工作内容都需要用心去做，小事情做好了才能不断承担更大的责任，苦哈哈、累哈哈也认了，毕竟成长也快。")]),v._v(" "),s("p",[v._v("我举这两个例子想要说明什么呢？"),s("strong",[v._v("其实资源配置的习惯决定人生成就。")])]),v._v(" "),s("p",[v._v("我们每天应付工作，每天正常工作时间甚至不够应付当天的任务，这怎么办？还能怎么办，只能在自己生活的时间里「借」，压缩睡眠，休息，学习，陪伴家人的时间。")]),v._v(" "),s("p",[v._v("认识到这种问题的危害性，就需要立刻采取果断行动，让时间和任务之间产生翻转，能让工作时间完成特定的工作任务，还可以有时间结余。关键就是只做少数重要的事情，而不是要平均用力。")]),v._v(" "),s("p",[v._v("从长期来看，每天剩余出来的时间可以做什么？可以做两件事，第一个用于个人效率、管理能力的学习和提升，还是那句话「认知提升带来效率提升」管理不是狭隘的管别人，知识管理、时间管理、目标管理都是现代职场人需要具备的核心能力。")]),v._v(" "),s("p",[v._v("我之前看过一个观点：一天中只有早上出门前的时间是属于你的，你出门之后的时间都是不属于你的，我们都习惯了加班。很多事情是不可控的。一个会议原定半小时，开个2小时的场景我也见过，这种时间黑洞怎么能不让人心生恐惧，所以早起是很重要的，早起的质量很多时候能够决定人生的质量，清晨早起到上班这块时间，你可以探索一些人生的意外惊喜。")]),v._v(" "),s("p",[s("img",{attrs:{src:t(355),alt:"四象限时间管理"}})]),v._v(" "),s("p",[v._v("四象限工作法也是是很有指导意义的，在四象限图中可以清晰的展示出，如果我们获得「自主时间」，就需要从第三象限（不紧急，不重要）、第四象限（紧急不重要）中压缩水分，把时间投入到第二现象（不紧急但是重要）。其中，第三象限的事情多属于休闲娱乐，在工作时间最好不要做，第四象限的事情要么通过工具去做，要么交给团队或者助手去做。")]),v._v(" "),s("p",[v._v("省出来的时间一定要投入第二象限，因为只有不紧急但重要的事情，才会产生长期影响。这一象限的事情包含自我教育、学习、投资、健康、培养关系等。")]),v._v(" "),s("p",[v._v("如果我们把每天、每件事时间分配连起来看，就能构成人生的选择。人生其实就是一次一次二选一的叠加，选择把时间精力投在A还是投资B，由我们内心的优先级和管理策略所决定。")]),v._v(" "),s("p",[v._v("如果我们不断的减少第三、第四象限的投入，把更多的资源投入第二象限。那么我们的一生会变得富有意义感，也会拥有更大的成就。而不是一天忙于琐事，最后一事无成。")]),v._v(" "),s("p",[v._v("每天上班前，抽出10分钟画出自己的四象限，耐心的梳理一下，相信我，你会有挺大的惊喜。")]),v._v(" "),s("p",[v._v("很多人总是说：「道理我都懂，就是做不到」，在我看来，其实他所谓的「道理都懂」，只是旁观而别人的答案而已，根本不是真的懂。")]),v._v(" "),s("p",[v._v("要想真的懂，第一步是自己参与思考，第二是一定要事上练。**否则，只是看书、旁观他人，自己最终会一事无成，还会抱怨：我花了那么多时间看书、看文章、上课，为什么收获甚少？")])])}),[],!1,null,null,null);_.default=p.exports}}]);